import React from 'react'


 //function to sort by MaxCP
 function compareCP(a,b){
    // converting to uppercase to have case-insensitive comparison
    const cp1 = a.MaxCP;
    const cp2 = b.MaxCP;

    let comparison = 0
    if(cp1 > cp2){
        comparison = -1
    } else if(cp1 < cp2){
        comparison = 1
    }
    return comparison
}

const SearchView = ({check, firstFiltered, secondFiltered, userInput}) => {
    let finalFiltered = check ? firstFiltered.slice(0,4).sort(compareCP) : secondFiltered

    const name = finalFiltered.map(item => {
        const regex = new RegExp(userInput, 'gi')
        const highlightName = item.Name.replace(regex, [<span class="hl">${
            userInput}</span>])
        console.log('highLightName', highlightName)
        return highlightName
    })

    return (
       <>
        {finalFiltered.length === 0 ? <div className="loader"></div> : ''}
        {finalFiltered.length > 0 ? 
          <ul className="suggestions">
                {finalFiltered.map(item => {
                return (
                    <li key={item.Number}>
                        <img
                            src={`${item.img}`}
                            alt={`${item.Name}`}
                        />
                        <div className="info">
                            <h1>{name}</h1>
                            <span className={`type ${item.Types[0]}`}>{item.Types[0]}</span>
                            {item.Types[1] === undefined ? '' : <span className={`type ${item.Types[1]}`}>{item.Types[1]}</span>}
                        </div>
                    </li>
                )
            })
        }
          </ul> 
        : 
        <ul className="suggestions">
          <li>
               <img
                   src="http://assets.pokemon.com/assets/cms2/img/pokedex/full/025.png"
                   alt="Pikachu"
               />
               <div className="info">
                       <h1><span className="hl">Pika</span>chu</h1>
                       <span className="type electric">Electric</span>
                       <span className="type normal">Normal</span>
               </div>
           </li>
           {finalFiltered === false ? 
              <li>
                <img src="https://cyndiquil721.files.wordpress.com/2014/02/missingno.png" alt="" />
                <div className="info">
                    <h1 className="no-results">
                        No results
                    </h1>
                </div>
            </li>
            : 
            ''
           }
        </ul>
        }
      </>

)}

export default SearchView;
