import React, { useEffect, useState } from 'react';
import Axios from 'axios'
import './App.css';

const URL_PATH = "https://gist.githubusercontent.com/bar0191/fae6084225b608f25e98b733864a102b/raw/dea83ea9cf4a8a6022bfc89a8ae8df5ab05b6dcc/pokemon.json";

const App = () => {
    const [ input, setInput ] = useState([])
    //const [ call, setCall ] = useState({})

    useEffect(() => {
        (async () => {
            const res = await Axios.get(URL_PATH);
            setInput(res.data)
        })();
    }, []);

    const catchText = async event => {
        let val = event.target.value
        let filterByname = input.filter(item => item.Name.includes(val))
        console.log('filterByname', filterByname)
        console.log('input', input)
       // let typesLength = input.filter(item => item.Types[0].includes("g"))
        let typesLength = input.map(item => item.Types.length)
        console.log('types length array', typesLength)

            let filteredByType0
            //if(typesLength[i] > 1){
              //  console.log('typesLength[i]', typesLength[i].length)
               // console.log('typesLength[i]', typesLength.length)
               // filteredByType0 = input.filter(item => (item.Types[0].includes(val) || item.Types[1].includes(val)))
            //}else {
                filteredByType0 = input.filter(item => item.Types[1] !== undefined ? (item.Types[0].to.includes(val) || item.Types[1].includes(val) || item.Name.includes(val)) : (item.Types[0].includes(val) || item.Name.includes(val)))
                console.log('filteredByType0', filteredByType0)


        }
        //if(typesLength)
        /*
            let filterByTypes = input
            .filter((element) => 
                element.Types.some((subElement) => subElement.includes(val)))
            .map(element => {
                let newElt = Object.assign({}, element); // copies element
                console.log('newElt', newElt)
                newElt.Types = newElt.Types.filter(subElement => subElement.includes(val)); 
                console.log('newElement', newElt)
                return newElt;
            });
        */


    return (
    <> 
        <label htmlFor="maxCP" className="max-cp">
            <input type="checkbox" id="maxCP" />
            <small>
                Maximum Combat Points
            </small>
        </label>
        <input type="text" className="input" placeholder="Pokemon or type" onChange={catchText}/>
        <div className="loader"></div>
        <ul className="suggestions">
            <li>
                <img src="http://assets.pokemon.com/assets/cms2/img/pokedex/full/025.png" alt="" />
                <div className="info">
                    <h1>
                        <span className="hl">Pika</span>chu</h1>
                    <span className="type electric">Electric</span>
                    <span className="type normal">Normal</span>
                </div>
            </li>
            <li>
                <img src="https://cyndiquil721.files.wordpress.com/2014/02/missingno.png" alt="" />
                <div className="info">
                    <h1 className="no-results">
                        No results
                    </h1>
                </div>
            </li>
        </ul>
    </>
    )
}
export default App;
