import React, { useEffect, useState } from 'react';
import Axios from 'axios'
import './App.css';

const URL_PATH = "https://gist.githubusercontent.com/bar0191/fae6084225b608f25e98b733864a102b/raw/dea83ea9cf4a8a6022bfc89a8ae8df5ab05b6dcc/pokemon.json";

const App = () => {
    const [ input, setInput ] = useState([])
    //const [ call, setCall ] = useState({})

    useEffect(() => {
        (async () => {
            const res = await Axios.get(URL_PATH);
            setInput(res.data)
        })();
    }, []);

    const catchText = async event => {
        let val = event.target.value
        filteringResult(val)
    }

    const filteringResult = (val) => {
        //console.log(Types.filter(name => name.includes(call)))
        //console.log(input)
        //console.log(val)
        let filterByname = input.filter(item => item.Name.includes(val))

        let filterByTypes = input.filter(item => item.Types[0].includes(val))

        let result =  input.filter(item => item.Types.map(value => value.includes(val)))
            console.log('result is', result)
        for(let i=0; i<input.length; i++){
                for(let j=0; j<input[i].Types.length; j++){
                    if(input[i].Types[j].includes(val)){
                        //console.log(input[i])
                        input[i].Add = "true"
                    }
                }
        }

        console.log('newInput ', input)

        //console.log(filterByTypes)
    }

    return (
    <> 
        <label htmlFor="maxCP" className="max-cp">
            <input type="checkbox" id="maxCP" />
            <small>
                Maximum Combat Points
            </small>
        </label>
        <input type="text" className="input" placeholder="Pokemon or type" onChange={catchText}/>
        <div className="loader"></div>
        <ul className="suggestions">
            <li>
                <img src="http://assets.pokemon.com/assets/cms2/img/pokedex/full/025.png" alt="" />
                <div className="info">
                    <h1>
                        <span className="hl">Pika</span>chu</h1>
                    <span className="type electric">Electric</span>
                    <span className="type normal">Normal</span>
                </div>
            </li>
            <li>
                <img src="https://cyndiquil721.files.wordpress.com/2014/02/missingno.png" alt="" />
                <div className="info">
                    <h1 className="no-results">
                        No results
                    </h1>
                </div>
            </li>
        </ul>
    </>
    )
}
export default App;
